mapper.select:
Select * From ( Select rownum rnum, a.* From ( Select board_id, post_id, lpad('┗ ', (level-1)*4, ' ')|| post_title as post_title, post_article, post_pid, post_groupid, post_date, post_del, post_userid From post Where board_id = #{board_id}
And post_title like '%' || #{searchBox} || '%'
Start With post_pid Is null Connect by prior post_id = post_pid Order Siblings By post_groupid desc, post_id asc) a) Where rnum Between #{page}*#{pageSize}-(#{pageSize}-1) And #{page}*#{pageSize}

mapper.update:
	Update post 

    post_title=#{post_title},
    post_article=#{post_article},
    post_del=#{post_del}

	Where post_id = #{post_id}
